mapper.select:
SELECT id, title, created_at FROM memo
title LIKE '%${searchText}%'
content LIKE '%${searchText}%'
(title LIKE '%${searchText}%' OR content LIKE '%${searchText}%')
AND id < #{lastIdx}
ORDER BY id DESC LIMIT #{limit}

SELECT id, title, content, created_at
FROM memo
WHERE id = #{memoId}

mapper.update:
UPDATE memo
SET title = #{title},
    content = #{content}
WHERE id = #{memoId}
